
/*
 * Global parameters for the heat map.
 */

export const CANVAS_WIDTH = 1600;
export const CANVAS_HEIGHT = 900;
export const PADDING_LEFT = 200;
export const PADDING_RIGHT = 150;
export const PADDING_BOT = 150;
export const PADDING_TOP = 200;

// computed parameters
export const PLOT_WIDTH = CANVAS_WIDTH - PADDING_LEFT - PADDING_RIGHT;
export const PLOT_HEIGHT = CANVAS_HEIGHT - PADDING_BOT - PADDING_TOP;
